'use strict';

var gulp = require('gulp'),
	through = require('through2'),
	htmllint = require('../src/index.js');

// default task
gulp.task('default', function() {
	return gulp.src('./*.html')
		.pipe(htmllint({
			'failOnError': true
		}))
		.on('error', function() {})
		.pipe(through.obj(function (file, enc, cb) {
				//console.log(file.htmllint.issues);
				cb()
		}))
		.on('error', function() {});
});
